	return x;

    }

arrays.type2:
			{a.rank == b.rank}{
	val x = new Array[Double](a.region);
	for (p in a.region){
		x(p) = a(p)- b(p);
	}
	return x;
}

arrays.type3:
			{
	val x = new Array[Double](b.region);
	for (p in b.region){
		x(p) = a- b(p);
	}
	return x;
}

arrays.type4:
			{
